import{_ as e,c as a,o as t,a as o}from"./app.55aea0e7.js";const f=JSON.parse('{"title":"Modifiers","description":"","frontmatter":{},"headers":[{"level":2,"title":"width / w","slug":"width-w","link":"#width-w","children":[]},{"level":2,"title":"height / h","slug":"height-h","link":"#height-h","children":[]},{"level":2,"title":"resize / s","slug":"resize-s","link":"#resize-s","children":[]},{"level":2,"title":"fit","slug":"fit","link":"#fit","children":[]},{"level":2,"title":"position / pos","slug":"position-pos","link":"#position-pos","children":[]},{"level":2,"title":"trim","slug":"trim","link":"#trim","children":[]},{"level":2,"title":"format / f","slug":"format-f","link":"#format-f","children":[]},{"level":2,"title":"quality / q","slug":"quality-q","link":"#quality-q","children":[]},{"level":2,"title":"rotate / r","slug":"rotate-r","link":"#rotate-r","children":[]},{"level":2,"title":"enlarge","slug":"enlarge","link":"#enlarge","children":[]},{"level":2,"title":"flip","slug":"flip","link":"#flip","children":[]},{"level":2,"title":"flop","slug":"flop","link":"#flop","children":[]},{"level":2,"title":"sharpen","slug":"sharpen","link":"#sharpen","children":[]},{"level":2,"title":"median","slug":"median","link":"#median","children":[]},{"level":2,"title":"gamma","slug":"gamma","link":"#gamma","children":[]},{"level":2,"title":"negate","slug":"negate","link":"#negate","children":[]},{"level":2,"title":"normalize","slug":"normalize","link":"#normalize","children":[]},{"level":2,"title":"threshold","slug":"threshold","link":"#threshold","children":[]},{"level":2,"title":"grayscale","slug":"grayscale","link":"#grayscale","children":[]},{"level":2,"title":"animated","slug":"animated","link":"#animated","children":[]}],"relativePath":"guide/modifiers.md","lastUpdated":1672970076000}'),r={name:"guide/modifiers.md"},p=o('<h1 id="modifiers" tabindex="-1">Modifiers <a class="header-anchor" href="#modifiers" aria-hidden="true">#</a></h1><h2 id="width-w" tabindex="-1"><code>width</code> / <code>w</code> <a class="header-anchor" href="#width-w" aria-hidden="true">#</a></h2><p>Resize the image to the specified width in pixels. The height is calculated with the same aspect ratio.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/width_200/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?width=200</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-resize#resize" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-resize#resize</a></p><h2 id="height-h" tabindex="-1"><code>height</code> / <code>h</code> <a class="header-anchor" href="#height-h" aria-hidden="true">#</a></h2><p>Resize the image to the specified height in pixels. The width is calculated with the same aspect ratio.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/height_200/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?height=200</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-resize#resize" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-resize#resize</a></p><h2 id="resize-s" tabindex="-1"><code>resize</code> / <code>s</code> <a class="header-anchor" href="#resize-s" aria-hidden="true">#</a></h2><p>Resize the image to the specified width and height in pixels. The aspect ratio is not preserved.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/s_200x200/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?resize=200x200</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-resize#resize" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-resize#resize</a></p><h2 id="fit" tabindex="-1"><code>fit</code> <a class="header-anchor" href="#fit" aria-hidden="true">#</a></h2><p>Resize the image to the specified width and height in pixels. The aspect ratio is preserved.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/s_200x200,fit_outside/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?resize=200x200&amp;fit=outside</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-resize#resize" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-resize#resize</a></p><h2 id="position-pos" tabindex="-1"><code>position</code> / <code>pos</code> <a class="header-anchor" href="#position-pos" aria-hidden="true">#</a></h2><p>Sets <code>position</code> option for <code>resize</code>. Only works with <code>fit</code> and <code>resize</code> modifiers.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/s_200x200,pos_top/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?resize=200x200&amp;position=top</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-resize#resize" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-resize#resize</a></p><h2 id="trim" tabindex="-1"><code>trim</code> <a class="header-anchor" href="#trim" aria-hidden="true">#</a></h2><p>Trim the image to remove any border that matches the specified background color.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/trim_ffffff/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?trim=ffffff</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-operation#trim" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-operation#trim</a></p><h2 id="format-f" tabindex="-1"><code>format</code> / <code>f</code> <a class="header-anchor" href="#format-f" aria-hidden="true">#</a></h2><p>Convert the image to the specified format. The format must be one of the following: <code>jpeg</code>, <code>jpg</code>, <code>png</code>, <code>webp</code>, <code>tiff</code>, <code>raw</code>, <code>gif</code>, <code>svg</code>, <code>heif</code>, <code>heic</code>, <code>avif</code>.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/format_jpg/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?format=jpg</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-output#toformat" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-output#toformat</a></p><h2 id="quality-q" tabindex="-1"><code>quality</code> / <code>q</code> <a class="header-anchor" href="#quality-q" aria-hidden="true">#</a></h2><p>Set the quality of the output image. This is only applicable to JPEG and WebP images.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/quality_50/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?quality=50</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-output#jpeg" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-output#jpeg</a></p><h2 id="rotate-r" tabindex="-1"><code>rotate</code> / <code>r</code> <a class="header-anchor" href="#rotate-r" aria-hidden="true">#</a></h2><p>Rotate the image by the specified number of degrees.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/rotate_90/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?rotate=90</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-operation#rotate" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-operation#rotate</a></p><h2 id="enlarge" tabindex="-1"><code>enlarge</code> <a class="header-anchor" href="#enlarge" aria-hidden="true">#</a></h2><p>Enlarge the output image to be at least as wide and as high as the specified width and height in pixels. The aspect ratio is not preserved.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/enlarge_200x200/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?enlarge=200x200</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-resize#resize" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-resize#resize</a></p><h2 id="flip" tabindex="-1"><code>flip</code> <a class="header-anchor" href="#flip" aria-hidden="true">#</a></h2><p>Flip the image about the vertical Y axis. This is equivalent to a horizontal flip if the image is displayed in the correct orientation.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/flip/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?flip</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-operation#flip" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-operation#flip</a></p><h2 id="flop" tabindex="-1"><code>flop</code> <a class="header-anchor" href="#flop" aria-hidden="true">#</a></h2><p>Flip the image about the horizontal X axis. This is equivalent to a horizontal flip.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/flop/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?flop</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-operation#flop" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-operation#flop</a></p><h2 id="sharpen" tabindex="-1"><code>sharpen</code> <a class="header-anchor" href="#sharpen" aria-hidden="true">#</a></h2><p>Sharpen the image. This can be used to increase the perceived sharpness of an image.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/sharpen/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?sharpen</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-operation#sharpen" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-operation#sharpen</a></p><h2 id="median" tabindex="-1"><code>median</code> <a class="header-anchor" href="#median" aria-hidden="true">#</a></h2><p>Apply a median filter to the image. This can be used to reduce noise in an image.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/median/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?median</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-operation#median" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-operation#median</a></p><h2 id="gamma" tabindex="-1"><code>gamma</code> <a class="header-anchor" href="#gamma" aria-hidden="true">#</a></h2><p>Apply gamma correction to the image. This can be used to adjust the overall brightness of an image.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/gamma_1.5/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?gamma=1.5</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-operation#gamma" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-operation#gamma</a></p><h2 id="negate" tabindex="-1"><code>negate</code> <a class="header-anchor" href="#negate" aria-hidden="true">#</a></h2><p>Negate the image. This can be used to invert the colors of an image.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/negate/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?negate</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-operation#negate" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-operation#negate</a></p><h2 id="normalize" tabindex="-1"><code>normalize</code> <a class="header-anchor" href="#normalize" aria-hidden="true">#</a></h2><p>Normalize the image. This can be used to improve the contrast in an image.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/normalize/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?normalize</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-operation#normalize" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-operation#normalize</a></p><h2 id="threshold" tabindex="-1"><code>threshold</code> <a class="header-anchor" href="#threshold" aria-hidden="true">#</a></h2><p>Threshold the image. This can be used to convert an image to black and white.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/threshold_128/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?threshold=128</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-operation#threshold" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-operation#threshold</a></p><h2 id="grayscale" tabindex="-1"><code>grayscale</code> <a class="header-anchor" href="#grayscale" aria-hidden="true">#</a></h2><p>Convert the image to grayscale. This can be used to remove color from an image.</p><p><strong>Example (Path modifier)</strong>:<br><code>http://localhost:1337/uploads/grayscale/buffalo.png</code></p><p><strong>Example (Query parameter)</strong>:<br><code>http://localhost:1337/uploads/buffalo.png?grayscale</code></p><p><strong>Sharp documentation</strong>:<br><a href="https://sharp.pixelplumbing.com/api-operation#greyscale" target="_blank" rel="noreferrer">https://sharp.pixelplumbing.com/api-operation#greyscale</a></p><h2 id="animated" tabindex="-1"><code>animated</code> <a class="header-anchor" href="#animated" aria-hidden="true">#</a></h2><p><strong>experimental</strong></p>',98),i=[p];function l(n,s,h,d,c,g){return t(),a("div",null,i)}const u=e(r,[["render",l]]);export{f as __pageData,u as default};
